import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import '../components/css/main.css';
import { validateEmail, validatePassword } from '../components/js/main';

const Signup = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [email, setEmail] = useState('');

  let [emailError,setEmailError] = useState('');
  let [passwordError,setPasswordError] = useState('');

  const apiEndpoint = 'https://2a2tyby9f2.execute-api.ap-southeast-2.amazonaws.com/Test/';

  const handleSubmit = async(e) => {
    e.preventDefault();
    //Validate email and password locally
    let localValidation = true;
    if (validateEmail(email) == false){
      setEmailError('Error: Email is invalid');
      localValidation = false;
    }
    else{
      setEmailError = '';
    }
    //Check passwords Match
    if (password != password2){
      setPasswordError('Error: Passwords do not match');
      localValidation = false;
    }
    else if(validatePassword(password) != true){
      setPasswordError('Error: Password is not valid');
      localValidation=false;
    }
    else{
      setPasswordError = '';
    }
    if (localValidation == false){
      return
    }
    //Send Payload to API Gateway
    const payload = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password
    }

    try {
      const response = await fetch(apiEndpoint, {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(payload)
      });

      if (response.ok) {
        console.log('API call successful');
        const data = await response.json(); // Extract the response data
        console.log('Response data:', data);
    } else {
        console.error('API call failed');
        console.log('Response status:', response.status);
    }
    } catch (error) {
    console.error('An error occurred:', error);
    }
  };


  return(
    <div className='container'>
      <div className='header'>
        <div className='text'>Sign Up</div>
        <div className='underline'></div>
      </div>
      <div className='inputs'>
        <div className='input'>
          <input
            type="text"
            placeholder="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="text"
            placeholder="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <span className='error'>{emailError}</span>
        <div className='input'>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="password"
            placeholder="Confirm Password"
            value={password2}
            onChange={(e) => setPassword2(e.target.value)}
          />
        </div>
        <span className='error'>{passwordError}</span>
      </div>
      <div className='subheader'>Returning User? <Link to='/login'>Click Here!</Link></div>
      <div className='submit-container'>
      <button onClick={handleSubmit} className='submit'>Sign Up</button>
      </div>
    </div>
  )
}

export default Signup
