import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { AccessTokenProvider, TokenRefresher, useAccessToken } from './components/js/main.js';
import Signup from './pages/signup';
import Login from './pages/login';
import Verify from './pages/verify';
import ListStudents from './pages/listStudents';
import AddStudent from './pages/addStudent';
import DeleteStudent from './pages/deleteStudent';
import PrivateRoute from './components/PrivateRoute';
import PublicRoute from './components/PublicRoute';

function App() {
  const { accessToken } = useAccessToken();
  const isLoggedIn = !!accessToken;

  return (
    <div>
    <AccessTokenProvider>
      {isLoggedIn && <TokenRefresher isLoggedIn={isLoggedIn} />}
      <BrowserRouter>
      <Routes>
          <Route path="/" element={<Navigate replace to='/login' />} />
          <Route path="login" element={
            <PublicRoute>
              <Login />
            </PublicRoute>
          } />
          <Route path="signup" element={
            <PublicRoute>
              <Signup />
            </PublicRoute>
          } />
          <Route path="verify" element={
            <PublicRoute>
              <Verify />
            </PublicRoute>
          } />
          <Route path="/listStudents" element={
            <PrivateRoute>
              <ListStudents />
            </PrivateRoute>
          } />
          <Route path="/addStudent" element={
            <PrivateRoute>
              <AddStudent />
            </PrivateRoute>
          } />
          <Route path="/deleteStudent" element={
            <PrivateRoute>
              <DeleteStudent />
            </PrivateRoute>
          } />
          <Route path="*" element={
            isLoggedIn ? <Navigate replace to="/listStudents" /> : <Navigate replace to="/login" />
          } />
        </Routes>
      </BrowserRouter>
    </AccessTokenProvider>
    </div>
  );
}

export default App;
