import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import '../components/css/main.css'
import { isValidDateValue } from '@testing-library/user-event/dist/utils';

function validName(input) {
  for (const char of input) {
      if (!(char >= "a" && char <= "z") &&
          !(char >= "A" && char <= "Z")) {
          return false;
      }
  }
  return true;
}


const CreateUser = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [dob, setDOB] = useState('');
  const [id, setID] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to the server)
    console.log('First Name:', firstName);
    console.log('Last Name:', lastName);
    console.log('D.O.B:', dob);
    console.log('ID:', id);
    if(validName(firstName == false)){
      console.log('Invalid First name')
    }
    if(validName(lastName==False)){
      console.log('Invalid Last Name')
    }
    if(isValidDateValue(dob)==False){
      console.log('Invalid Date')
    }

  };

  return(
    <div className='container'>
      <div className='header'>
        <div className='text'>Create Student</div>
        <div className='underline'></div>
      </div>
      <div className='inputs'>
        <div className='input'>
          <input
            type="text"
            placeholder="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="text"
            placeholder="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="date"
            placeholder="Date of Birth"
            max={new Date().toISOString().split("T")[0]}
            value={dob}
            onChange={(e) => setDOB(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="text"
            placeholder="Student ID(Integer Only)"
            value={id}
            onChange={(e) => setID(e.target.value)}
          />
        </div>
      </div>
      <div className='submit-container'>
      <button onClick={handleSubmit} className='submit'>Sign Up</button>
      </div>
    </div>
  )
}

export default CreateUser
