import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { AccessTokenProvider, TokenRefresher } from './components/js/main.js';
import Signup from './pages/signup';
import Login from './pages/login';
import Verify from './pages/verify';
import Teacher from './pages/teacher';
import AddStudent from './pages/addStudent';
import DeleteStudent from './pages/deleteStudent';
import ProtectedRoute from './components/ProtectedRoute';
import PublicRoute from './components/PublicRoute';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Example function to check login status
  const checkLoginStatus = () => {
    const token = localStorage.getItem('accessToken');
    setIsLoggedIn(!!token);
  };

  useEffect(() => {
    checkLoginStatus();
  }, []);

  return (
    <div>
    <AccessTokenProvider>
      {isLoggedIn && <TokenRefresher isLoggedIn={isLoggedIn} />}
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Navigate replace to='/login' />} />
          <Route path="login" element={
            <PublicRoute isLoggedIn={isLoggedIn}>
              <Login setIsLoggedIn={setIsLoggedIn} />
            </PublicRoute>
          } />
          <Route path="signup" element={
            <PublicRoute isLoggedIn={isLoggedIn}>
              <Signup />
            </PublicRoute>
          } />
          <Route path="verify" element={
            <PublicRoute isLoggedIn={isLoggedIn}>
              <Verify />
            </PublicRoute>
          } />
          <Route path="teacher/*" element={
            <ProtectedRoute isLoggedIn={isLoggedIn}>
              <Teacher />
            </ProtectedRoute>
          }>
            <Route path="addStudent" element={<AddStudent />} />
            <Route path="deleteStudent" element={<DeleteStudent />} />
          </Route>
          <Route path="*" element={
            isLoggedIn ? <Navigate replace to="/teacher" /> : <Navigate replace to="/login" />
          } />
        </Routes>
      </BrowserRouter>
    </AccessTokenProvider>
    </div>
  );
}

export default App;
