import React, { useState, useEffect } from 'react';
import {BrowserRouter, Routes, Route, Navigate} from 'react-router-dom';
import { AccessTokenProvider } from '..src/components/js/main.js';
import Signup from './pages/signup'
import Login from './pages/login'
import Verify from './pages/verify'
import Teacher from './pages/teacher';
import AddStudent from './pages/addStudent';
import DeleteStudent from './pages/deleteStudent';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    // Retrieve the login status from local storage
    const saved = localStorage.getItem('isLoggedIn');
    return saved === 'true';
  });

  useEffect(() => {
    // Save the login status to local storage whenever it changes
    localStorage.setItem('isLoggedIn', isLoggedIn);
  }, [isLoggedIn]);
  return (
    <div>
    <AccessTokenContext.Provider value={{ accessToken, setAccessToken }}>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Navigate replace to='/login' />} />
        <Route path="login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="signup" element={<Signup />} />
        <Route path="verify" element={<Verify />} />
        <Route path="teacher/*" element={
          isLoggedIn ? <Teacher /> : <Navigate replace to='/login' />
        }>
          <Route path="addStudent" element={<AddStudent />} />
          <Route path="deleteStudent" element={<DeleteStudent />} />
        </Route>
        <Route path="*" element={<Navigate replace to='/login' />} />
      </Routes>
    </BrowserRouter>
    </AccessTokenContext.Provider>
    </div>
  );
}

export default App;
