import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import '../components/css/main.css'

const Signup = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [email, setEmail] = useState('');

  const apiEndpoint = 'https://2a2tyby9f2.execute-api.ap-southeast-2.amazonaws.com/Test';

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to the server)
    //Validate Inputs locally first
    console.log('First Name:', firstName);
    console.log('Last Name:', lastName);
    //Validate Email
    console.log('Email:', email);
    //Check passwords Match
    //Check PW requirements
    // At least 1 number
    // At least 1 special case
    // At least 1 Upper case
    // At least 1 lower case
    console.log('Password:', password);
    console.log('Password2:', password2);

    //Send Payload to API Gateway
    const payload = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password
    }

    fetch(apiEndpoint,{
      method: 'POST',
      headers: {
        'Content-type': 'application/json',
        'Access-Control-Allow-Origin': '*'
      },
      body: JSON.stringify(payload)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:',data);
    })
    .catch((error) => {
      console.error('Error:',error);
    });
  };

  return(
    <div className='container'>
      <div className='header'>
        <div className='text'>Sign Up</div>
        <div className='underline'></div>
      </div>
      <div className='inputs'>
        <div className='input'>
          <input
            type="text"
            placeholder="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="text"
            placeholder="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className='input'>
          <input
            type="password"
            placeholder="Confirm Password"
            value={password2}
            onChange={(e) => setPassword2(e.target.value)}
          />
        </div>
      </div>
      <div className='subheader'>Returning User? <Link to='/login'>Click Here!</Link></div>
      <div className='submit-container'>
      <button onClick={handleSubmit} className='submit'>Sign Up</button>
      </div>
    </div>
  )
}

export default Signup
